package javabeginner1;

public class IntValues2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int max=Integer.MAX_VALUE;
		int min=Integer.MIN_VALUE;
		int maxplus=max+2;
		int mindecr=min-2;
		System.out.println("int 型的最大值："+max);
		System.out.println("int 型的最小值："+min);
		System.out.println("int 型的最大值+1："+(max+1));
		System.out.println("int 型的最小值-1："+(min-1));
		System.out.println("int 型的最大值+2："+maxplus);
		System.out.println("int 型的最小值-2："+mindecr);
	}
}
