package javabeginner1;

public class Operator1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=3+5;
		int m=+2;
		String str1="i+",str2="m=";
		String str3=str1+str2;
		System.out.println(str3+(i+m));
		int n=i-m;
		int k=-i;
		int h=i*k;
		double d=h*3.00;
		System.out.println("n="+n+"\th="+h+"\td="+d);
		int e=10%6;
		double d1=10/3.0;
		System.out.println("e="+e+"\tf="+d1);
	}

}
