package javabeginner1;

public class Operator6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a1=12>>2;
		System.out.println("12>>2="+a1);
		int a2=-12>>2;
		System.out.println("-12>>2="+a2);
		int b1=12>>>2;
		System.out.println("12>>>2="+b1);
		int b2=-12>>>2;
		System.out.println("-12>>>2="+b2);
		int c1=12<<2;
		System.out.println("12<<2="+c1);
		int c2=-12<<2;
		System.out.println("-12<<2="+c2);
	}

}
